/* This is a minimal jQuery stub for the debug page */
/* It will be used to make the debug page interactive */
window.jQuery = window.$ = function(selector) {
    const elements = document.querySelectorAll(selector);
    return {
        click: function(callback) {
            elements.forEach(el => el.addEventListener('click', callback));
            return this;
        },
        html: function(content) {
            if (content === undefined) {
                return elements[0] ? elements[0].innerHTML : '';
            }
            elements.forEach(el => el.innerHTML = content);
            return this;
        },
        text: function(content) {
            if (content === undefined) {
                return elements[0] ? elements[0].textContent : '';
            }
            elements.forEach(el => el.textContent = content);
            return this;
        },
        ready: function(callback) {
            if (document.readyState !== 'loading') {
                callback();
            } else {
                document.addEventListener('DOMContentLoaded', callback);
            }
            return this;
        }
    };
}; 